INSERT INTO `wp_eo_venuemeta` VALUES (1,15,'_address','Lassallestraße 50'),(2,15,'_city','Erfurt'),(3,15,'_postcode','99086'),(4,15,'_country','Deutschland'),(5,15,'_lat','50.988937'),(6,15,'_lng','11.031460'),(7,15,'_state','Thüringen'),(8,15,'_description','<div>\r\n<div>\r\n\r\nAm Anfang standen gekündigte Mietverträge und eine Zwangssanierung, daraus entstand der Wunsch über den eigenen Wohnraum gemeinsam und selbständig entscheiden zu können. Nach zweijährigem Vorlauf mit Gruppenfindung, Finanzierungsplänen, Haussuche, -kauf und Sanierung gibt es seit Anfang 2013 in Erfurt das erste selbst verwaltete Hausprojekt wohnopolis im Verbund des Mietshäuser Syndikats. Mit der finanziellen und praktischen Hilfe vieler Unterstützer_innen ist in der Lassallestraße 50 auf drei Etagen Wohnraum für 9 Erwachsene und ein Kind sowie ein öffentlich genutzer Raum namens [L50] im Erdgeschoss entstanden, über den Ihr auf <a href=\"http://l50.wohnopolis.de/\" target=\"_blank\">dieser Seite</a> mehr erfahren könnt.\r\n\r\n</div>\r\n</div>'),(9,17,'_address','Adlerstr. 12'),(10,17,'_city','Freiburg'),(11,17,'_state','Baden-Württemberg'),(12,17,'_postcode','79098'),(13,17,'_country','Deutschland'),(14,17,'_lat','47.992961'),(15,17,'_lng','7.840557');
