INSERT INTO `wp_events` VALUES (1,'Syndikatsbar Leipzig','Y','Zollschuppenstr. 11 04229 Leipzig',1,'jeden zweiten Donnerstag im Monat in der Zollschuppenstr. 11 -  04229 Leipzig\r\n<h3>Mietshäuser Syndikat Leipzig Beratung</h3>\r\nIhr seid eine Projektgruppe und habt Interessen am Syndikat?\r\n<ul>\r\n	<li>Lernt Leute (oder vielleicht kennt ihr schon jemanden) aus einem der <a title=\"Leipziger Syndikatsprojekte\" href=\"http://www.zollschuppenverein.de/wiki/index.php/Leipziger_Syndikatsprojekte\">Leipziger Syndikatsprojekte</a> kennen und lasst euch von ihnen erzählen wie der Alltag ist und was zum Syndikat gehört</li>\r\n	<li>Wer gerne liest: es gibt eine tolle <a href=\"http://www.syndikat.org/s/service/broschuere_nr5.pdf\" rel=\"nofollow\">Syndikatsbroschüre (pdf)</a> in der die Grundzüge erklärt werden.</li>\r\n	<li>Wer gerne schaut: <a title=\"Mietshäuse Syndikat Videos\" href=\"http://www.zollschuppenverein.de/wiki/index.php/Mietsh%C3%A4use_Syndikat_Videos\">Mietshäuse Syndikat Videos</a></li>\r\n	<li>Kommt zur monatlichen (2. Donnerstag) <b>Syndikatsbar</b> um in lockerer Runde Fragen zum Syndikat zu klären und Leute kennen zu lernen.</li>\r\n	<li>Ihr habt schon eine Vorstellung vom Syndikat und wart idealerweise auch schon einmal zur Syndikatsbar? Ihr wollt jetzt konkreter werden dann schreibt eine Mail an die <a href=\"mailto:beratung-leipzig@syndikat.org\" rel=\"nofollow\">Leipziger Beratung</a>) und wir machen einen Termin mit euch aus. Die Beratung ist ehrenamtliche Arbeit und es gibt viel zu tun, also nicht wundern wenn es einige Tage dauert bis eine Antwort kommt :)</li>\r\n</ul>','Zurückliegend!','http://www.zollschuppenverein.de/','N',1375992000,1376002800,'Maaster','yes','yes'),(2,'Syndikatsbar Leipzig','Y','Zollschuppenstr. 11 04229 Leipzig',1,'jeden zweiten Donnerstag im Monat in der Zollschuppenstr. 11 -  04229 Leipzig\r\n<h3>Mietshäuser Syndikat Leipzig Beratung</h3>\r\nIhr seid eine Projektgruppe und habt Interessen am Syndikat?\r\n<ul>\r\n	<li>Lernt Leute (oder vielleicht kennt ihr schon jemanden) aus einem der <a title=\"Leipziger Syndikatsprojekte\" href=\"http://www.zollschuppenverein.de/wiki/index.php/Leipziger_Syndikatsprojekte\">Leipziger Syndikatsprojekte</a> kennen und lasst euch von ihnen erzählen wie der Alltag ist und was zum Syndikat gehört</li>\r\n	<li>Wer gerne liest: es gibt eine tolle <a href=\"http://www.syndikat.org/s/service/broschuere_nr5.pdf\" rel=\"nofollow\">Syndikatsbroschüre (pdf)</a> in der die Grundzüge erklärt werden.</li>\r\n	<li>Wer gerne schaut: <a title=\"Mietshäuse Syndikat Videos\" href=\"http://www.zollschuppenverein.de/wiki/index.php/Mietsh%C3%A4use_Syndikat_Videos\">Mietshäuser Syndikat Videos</a></li>\r\n	<li>Kommt zur monatlichen (2. Donnerstag) <b>Syndikatsbar</b> um in lockerer Runde Fragen zum Syndikat zu klären und Leute kennen zu lernen.</li>\r\n	<li>Ihr habt schon eine Vorstellung vom Syndikat und wart idealerweise auch schon einmal zur Syndikatsbar? Ihr wollt jetzt konkreter werden dann schreibt eine Mail an die <a href=\"mailto:beratung-leipzig@syndikat.org\" rel=\"nofollow\">Leipziger Beratung</a> und wir machen einen Termin mit euch aus. Die Beratung ist ehrenamtliche Arbeit und es gibt viel zu tun, also nicht wundern wenn es einige Tage dauert bis eine Antwort kommt :)</li>\r\n</ul>','Zurückliegend!','http://www.zollschuppenverein.de/','N',1379016000,1379026800,'Maaster','yes','yes'),(3,'Recht-auf-Stadt Netzwerktreffen, Freiburg','Y','',1,'Jeden 2. Freitag im Monat um 20 Uhr im Syndikats-Büro auf dem Grethergelände, Adlerstr. 12, 79098 Freiburg\r\n\r\n\"Recht auf Stadt\" (RaS) ist ein Netzwerk aus <a href=\"http://www.rechtaufstadt-freiburg.de/links/\">Freiburger Initiativen</a> und Einzelpersonen, die sich für <a href=\"http://www.rechtaufstadt-freiburg.de/2012/10/demo-bezahlbarer-wohnraum-ist-die-halbe-miete/\">\"bezahlbaren Wohnraum\"</a>, städtische Freiräume, die Erhaltung von öffentlichen Grünflächen und eine wirklich demokratische Stadt einsetzen; für das <a href=\"http://gentrificationblog.wordpress.com/2010/07/07/recht-auf-stadt-mehr-als-ein-guter-slogan/\">Recht auf Stadt</a> für alle BewohnerInnen – gegen Gentrification und neoliberale Stadtentwicklung.  Alle Interessierten sind herzlich eingeladen!','Zurückliegend!','http://www.rechtaufstadt-freiburg.de/','N',1376078400,1376078400,'Maaster','yes','yes'),(4,'Klausurwochenende der AGs in Frankfurt','N','',1,'','Zurückliegend!','','Y',1380931200,1381017600,'Maaster','yes','yes'),(5,'Mitgliederversammlung Mietshäuser Syndikat Lübeck','N','',1,'Hausprojekt \"Freie Hütte\" in Lübeck\r\ndas erste Mietshäuser-Syndikat-Projekt in Schleswig-Holstein\r\n\r\nfreie hütte e.V. \r\nFritz-Reuter-Str. 9, 23564 Lübeck\r\n0451- 48939032','Zurückliegend!','','N',1382745600,1382745600,'Maaster','yes','yes');
